import React, { useRef, useState, useEffect } from "react";
import { Button, Row, Col, Card, Form } from "react-bootstrap";

import { Link, useNavigate, Navigate } from "react-router-dom";

import { getUser } from "../configFiles/services";
import sweet from "sweetalert2";

import EmailIcon from "@mui/icons-material/Email";
import InputAdornment from "@mui/material/InputAdornment";
import TextField from "@mui/material/TextField";
import FormControl from "@mui/material/FormControl";
import { Alert } from "@mui/material";
const regForEmail = RegExp(/^[^\s@]+@[^\s@]+\.[^\s@]+$/);
const regForPass = RegExp(
  /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{7,15}$/
);

function Login() {
  return (
    <div>
      {show == true && (
        <Alert
          serverity="error"
          className="fw-bold"
          style={{ fontSize: "25px" }}
        >
          All fields are required
        </Alert>
      )}
      <Row style={{ width: "100%" }}>
        <Col style={{ width: "50%" }}>
          <FormControl
            sx={{ border: "2px solid rgb(223,223,223)", width: "90%" }}
            className="p-3 my-3 py-5"
          >
            <h1>Login to neostore</h1>
            <TextField
              style={{
                width: "100%",
                border: "1px solid rgb(223,223,223)",
                outline: "none",
              }}
              className="my-4"
              id="outlined-basic"
              variant="outlined"
              placeholder="email"
              name="email"
              onChange={handleLoginData}
              required
              InputProps={{
                endAdornment: (
                  <InputAdornment position="start">
                    <EmailIcon />
                  </InputAdornment>
                ),
              }}
            ></TextField>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput2">
              <TextField
                style={{
                  width: "100%",
                  border: "1px solid rgb(223,223,223)",
                  outline: "none",
                }}
                className="my-4"
                id="outlined-basic"
                variant="outlined"
                placeholder="Password"
                name="password"
                type={password}
                onChange={handleLoginData}
                required
              ></TextField>
              <div
                style={{
                  position: "absolute",
                  rigth: "37px",
                  bottom: "140px",
                  fontSize: "21px",
                  cursor: "pointer",
                  fontWeight: "bold",
                }}
              >
                <i
                  onClick={Eye}
                  className={`fa ${eye ? "fa-eye-slash" : "fa-eye"}`}
                ></i>
              </div>
            </Form.Group>
            <Button
              className="text-black"
              style={{ width: "15%", backgroundColor: "grey", border: "none" }}
              onClick={loggedIn}
            >
              Login
            </Button>
          </FormControl>
          <Link
            to="/register"
            style={{
              marginLeft: "10px",
              textDecoration: "none",
              color: "black",
            }}
          >
            Register now
          </Link>
          <Link
            to="/forgotpasssword"
            style={{
              marginLeft: "10px",
              textdecoration: "none",
              color: "black",
            }}
          >
            Forgot passsword?
          </Link>
        </Col>
      </Row>
      {state.flag == 1 && <Navigate to="/dashboard" />}
    </div>
  );
}

export default Login;
